import { useEffect } from "react";
import {
  BrowserRouter,
  Routes,
  Route,
  useLocation
} from 'react-router-dom';

import Home from "./scenes/home/Home";

 const ScrollToTop = () => {
    const {pathname} = useLocation();

    useEffect(() => {
      window.scrollTo(0, 0);

    }, [pathname])

    return null;

 }


function App() {
  return (
    <div className="app">
        <BrowserRouter>
          <ScrollToTop />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="item/:itemId" element={<Item />} />
          </Routes>
        </BrowserRouter>
    </div>
  );
}

export default App;
